  }

  render() {
    const { value } = this.state;
    return (
      <Segment basic padded="very">
        <Header as="h2" textAlign="center">
          Team Selection Form
        </Header>
        <Form>
          <Grid columns={2} padded>
            <Grid.Column>
              <Form.Field label="Choose 2 Goal Keepers" control={DropdownGK} onChange={this.handleGKChange} />
            </Grid.Column>
            <Grid.Column>
              <Form.Field label="Choose 4 Defense" control={DropdownDEF} />
            </Grid.Column>
            <Grid.Column>
              <Form.Field label="Choose 4 Midfielders" control={DropdownMID} />
            </Grid.Column>
            <Grid.Column>
              <Form.Field label="Choose 3 Forwards" control={DropdownFWD} />
            </Grid.Column>

            <div>
              {
                //<Form.Button>Submit</Form.Button>
              }
            </div>
          </Grid>
        </Form>
        <Modal
          trigger={
            <div id="button">
              <Button style={styles} color="blue">
                Submit
        </Button>
            </div>
          }>
          <Modal.Header>Select a Photo</Modal.Header>
          <Modal.Content image>
            <Image wrapped size="medium" src="/images/avatar/large/rachel.png" />
            <Modal.Description>
              <Header>Score of the team you picked </Header>
              <p>These are the players your team was made of</p>
              <p>Is it okay to use this photo?</p>
            </Modal.Description>
          </Modal.Content>
        </Modal>
      </Segment>
    );
  }
}

export default FormExampleSubcomponentControl;
