import React, { Component } from "react";
import { Form, GridColumn } from "semantic-ui-react";
import DropdownGK from "./DropdownGK.js";
import DropdownDEF from "./DropdownDEF.js";
import DropdownMID from "./DropdownMID.js";
import DropdownFWD from "./DropdownFWD.js";
import { Container, Divider, Grid, Header, Image } from "semantic-ui-react";
import { Segment } from "semantic-ui-react";
import { Button, Modal } from "semantic-ui-react";
import { Dropdown } from 'semantic-ui-react';
import PlayersGK from './player_gk.json';
import PlayersDef from './player_def.json';
import PlayersFwd from './player_fwd.json';
import PlayersMid from './player_mid.json';
import PlayerScores from './combined_gw_2.json';

class FormExampleSubcomponentControl extends Component {
  state = {
    goalkeeper1: "",
    goalkeeper2: "",
    defender1: "",
    defender2: "",
    defender3: "",
    defender4: "",
    midfielder1: "",
    midfielder2: "",
    midfielder3: "",
    midfielder4: "",
    forward1: "",
    forward2: "",
    forward3: "",
    gameWeek: 1,
    score: 0,
    styles: {
      position: "relative",
      left: 500,
      right: 10,
      bottom: 30,
    },
  }


  handleChange1 = (event, data) => {
    this.setState({ goalkeeper1: data.value });
  }
  handleChange2 = (event, data) => {
    this.setState({ goalkeeper2: data.value });
  }

  handleChangeDef1 = (event, data) => {
    this.setState({ defender1: data.value });
  }

  handleChangeDef2 = (event, data) => {
    this.setState({ defender2: data.value });
  }

  handleChangeDef3 = (event, data) => {
    this.setState({ defender3: data.value });
  }

  handleChangeDef4 = (event, data) => {
    this.setState({ defender4: data.value });
  }

  handleChangeMid1 = (event, data) => {
    this.setState({ midfielder1: data.value });
  }
  handleChangeMid2 = (event, data) => {
    this.setState({ midfielder2: data.value });
  }
  handleChangeMid3 = (event, data) => {
    this.setState({ midfielder3: data.value });
  }
  handleChangeMid4 = (event, data) => {
    this.setState({ midfielder4: data.value });
  }

  handleChangeForw1 = (event, data) => {
    this.setState({ forward1: data.value });
  }
  handleChangeForw2 = (event, data) => {
    this.setState({ forward2: data.value });
  }
  handleChangeForw3 = (event, data) => {
    this.setState({ forward3: data.value });
  }

  handleChangeGW = (event) => {
    this.setState({ gameWeek: event.target.value });
  }

  getScore = (event) => {
    var results = 0;

    //for goalkeeper 1
    const player_scores = PlayerScores;
    var team_list = [];
    team_list.push(this.state.goalkeeper1);
    team_list.push(this.state.goalkeeper2);
    team_list.push(this.state.defender1);
    team_list.push(this.state.defender2);
    team_list.push(this.state.defender3);
    team_list.push(this.state.defender4);
    team_list.push(this.state.midfielder1);
    team_list.push(this.state.midfielder2);
    team_list.push(this.state.midfielder3);
    team_list.push(this.state.midfielder4);
    team_list.push(this.state.forward1);
    team_list.push(this.state.forward2);
    team_list.push(this.state.forward3);
    for (var i = 0; i < player_scores.length; i++) {
      var player = player_scores[i];
      if (team_list.includes(player.name)) {
        if (Number(this.state.gameWeek) == 1) {
          results += Number(player.team_a_score_1) + Number(player.team_h_score_1);
        }
        if (Number(this.state.gameWeek) == 2) {
          results += Number(player.team_a_score_2) + Number(player.team_h_score_2);
        }
        if (Number(this.state.gameWeek) == 3) {
          results += Number(player.team_a_score_3) + Number(player.team_h_score_3);
        }
        if (Number(this.state.gameWeek) == 4) {
          results += Number(player.team_a_score_4) + Number(player.team_h_score_4);
        }
        if (Number(this.state.gameWeek) == 5) {
          results += Number(player.team_a_score_5) + Number(player.team_h_score_5);
        }
        if (Number(this.state.gameWeek) == 6) {
          results += Number(player.team_a_score_6) + Number(player.team_h_score_6);
        }
        if (Number(this.state.gameWeek) == 7) {
          results += Number(player.team_a_score_7) + Number(player.team_h_score_7);
        }
        if (Number(this.state.gameWeek) == 8) {
          results += Number(player.team_a_score_8) + Number(player.team_h_score_8);
        }
        if (Number(this.state.gameWeek) == 9) {
          results += Number(player.team_a_score_9) + Number(player.team_h_score_9);
        }
        if (Number(this.state.gameWeek) == 10) {
          results += Number(player.team_a_score_10) + Number(player.team_h_score_10);
        }
        if (Number(this.state.gameWeek) == 11) {
          results += Number(player.team_a_score_11) + Number(player.team_h_score_11);
        }
        if (Number(this.state.gameWeek) == 12) {
          results += Number(player.team_a_score_12) + Number(player.team_h_score_12);
        }
        if (Number(this.state.gameWeek) == 13) {
          results += Number(player.team_a_score_13) + Number(player.team_h_score_13);
        }
        if (Number(this.state.gameWeek) == 14) {
          results += Number(player.team_a_score_14) + Number(player.team_h_score_14);
        }
        if (Number(this.state.gameWeek) == 15) {
          results += Number(player.team_a_score_15) + Number(player.team_h_score_15);
        }
        if (Number(this.state.gameWeek) == 16) {
          results += Number(player.team_a_score_16) + Number(player.team_h_score_16);
        }
        if (Number(this.state.gameWeek) == 17) {
          results += Number(player.team_a_score_17) + Number(player.team_h_score_17);
        }
        if (Number(this.state.gameWeek) == 18) {
          results += Number(player.team_a_score_18) + Number(player.team_h_score_18);
        }
        if (Number(this.state.gameWeek) == 19) {
          results += Number(player.team_a_score_19) + Number(player.team_h_score_19);
        }
        if (Number(this.state.gameWeek) == 20) {
          results += Number(player.team_a_score_20) + Number(player.team_h_score_20);
        }

        if (Number(this.state.gameWeek) == 21) {
          results += Number(player.team_a_score_21) + Number(player.team_h_score_21);
        }

        if (Number(this.state.gameWeek) == 22) {
          results += Number(player.team_a_score_22) + Number(player.team_h_score_22);
        }

        if (Number(this.state.gameWeek) == 23) {
          results += Number(player.team_a_score_23) + Number(player.team_h_score_23);
        }

        if (Number(this.state.gameWeek) == 24) {
          results += Number(player.team_a_score_24) + Number(player.team_h_score_24);
        }

        if (Number(this.state.gameWeek) == 25) {
          results += Number(player.team_a_score_25) + Number(player.team_h_score_25);
        }
        if (Number(this.state.gameWeek) == 26) {
          results += Number(player.team_a_score_26) + Number(player.team_h_score_26);
        }
        if (Number(this.state.gameWeek) == 27) {
          results += Number(player.team_a_score_27) + Number(player.team_h_score_27);
        }
        if (Number(this.state.gameWeek) == 28) {
          results += Number(player.team_a_score_28) + Number(player.team_h_score_28);
        }
        if (Number(this.state.gameWeek) == 29) {
          results += Number(player.team_a_score_29) + Number(player.team_h_score_29);
        }
        if (Number(this.state.gameWeek) == 30) {
          results += Number(player.team_a_score_30) + Number(player.team_h_score_30);
        }
        if (Number(this.state.gameWeek) == 31) {
          results += Number(player.team_a_score_31) + Number(player.team_h_score_31);
        }
        if (Number(this.state.gameWeek) == 32) {
          results += Number(player.team_a_score_32) + Number(player.team_h_score_32);
        }
        if (Number(this.state.gameWeek) == 33) {
          results += Number(player.team_a_score_33) + Number(player.team_h_score_33);
        }

        if (Number(this.state.gameWeek) == 34) {
          results += Number(player.team_a_score_34) + Number(player.team_h_score_34);
        }
        if (Number(this.state.gameWeek) == 35) {
          results += Number(player.team_a_score_35) + Number(player.team_h_score_35);
        }
        if (Number(this.state.gameWeek) == 36) {
          results += Number(player.team_a_score_36) + Number(player.team_h_score_36);
        }
        if (Number(this.state.gameWeek) == 37) {
          results += Number(player.team_a_score_37) + Number(player.team_h_score_37);
        }
        if (Number(this.state.gameWeek) == 38) {
          results += Number(player.team_a_score_38) + Number(player.team_h_score_38);
        }


      }
    }
    this.setState({ score: results });
  }




  render() {
    const { value } = this.state;
    return (
      <Container>
        <Segment basic padded="very" color="purple" inverted>
          <Header as="h2" textAlign="center">
            Team Selection Form
        </Header>
          <Form inverted>
            <Grid columns={4} padded>
              <Grid.Column>
                <Form.Field padded="very">
                  <label>Choose a Game Week Between 1-38</label>
                  <input type="number" placeholder='1' min={1} max={38} onChange={this.handleChangeGW} />
                </Form.Field>
                <Form.Field padded="very">
                  <label>Choose Goal Keeper 1</label>
                  <Dropdown
                    placeholder='Player'
                    fluid
                    search
                    selection
                    options={PlayersGK}
                    onChange={this.handleChange1}
                  />
                </Form.Field>
                <Form.Field padded="very">
                  <label>Choose Goal Keeper 2</label>
                  <Dropdown
                    placeholder='Player'
                    fluid
                    search
                    selection
                    options={PlayersGK}
                    onChange={this.handleChange2}
                  />
                </Form.Field>
              </Grid.Column>

              <Grid.Column>
                <Form.Field padded="very">
                  <label>Choose Defender 1</label>
                  <Dropdown
                    placeholder='Player'
                    fluid
                    search
                    selection
                    options={PlayersDef}
                    onChange={this.handleChangeDef1}
                  />
                </Form.Field>
                <Form.Field padded="very">
                  <label>Choose Defender 2</label>
                  <Dropdown
                    placeholder='Player'
                    fluid
                    search
                    selection
                    options={PlayersDef}
                    onChange={this.handleChangeDef2}
                  />
                </Form.Field>
                <Form.Field padded="very">
                  <label>Choose Defender 3</label>
                  <Dropdown
                    placeholder='Player'
                    fluid
                    search
                    selection
                    options={PlayersDef}
                    onChange={this.handleChangeDef3}
                  />
                </Form.Field>
                <Form.Field padded="very">
                  <label>Choose Defender 4</label>
                  <Dropdown
                    placeholder='Player'
                    fluid
                    search
                    selection
                    options={PlayersDef}
                    onChange={this.handleChangeDef4}
                  />
                </Form.Field>
              </Grid.Column>
              <Grid.Column>
                <Form.Field padded="very">
                  <label>Choose Midfielder 1</label>
                  <Dropdown
                    placeholder='Player'
                    fluid
                    search
                    selection
                    options={PlayersMid}
                    onChange={this.handleChangeMid1} />
                </Form.Field>
                <Form.Field padded="very">
                  <label>Choose Midfielder 2</label>
                  <Dropdown
                    placeholder='Player'
                    fluid
                    search
                    selection
                    options={PlayersMid}
                    onChange={this.handleChangeMid2}
                  />
                </Form.Field>
                <Form.Field padded="very">
                  <label>Choose Midfielder 3</label>
                  <Dropdown
                    placeholder='Player'
                    fluid
                    search
                    selection
                    options={PlayersMid}
                    onChange={this.handleChangeMid3}
                  />
                </Form.Field>
                <Form.Field padded="very">
                  <label>Choose Midfielder 4</label>
                  <Dropdown
                    placeholder='Player'
                    fluid
                    search
                    selection
                    options={PlayersMid}
                    onChange={this.handleChangeMid4}
                  />
                </Form.Field>
              </Grid.Column>
              <Grid.Column>
                <Form.Field padded="very">
                  <label>Choose Forward 1</label>
                  <Dropdown
                    placeholder='Player'
                    fluid
                    search
                    selection
                    options={PlayersFwd}
                    onChange={this.handleChangeForw1}
                  />
                </Form.Field>
                <Form.Field padded="very">
                  <label>Choose Forward 2</label>
                  <Dropdown
                    placeholder='Player'
                    fluid
                    search
                    selection
                    options={PlayersFwd}
                    onChange={this.handleChangeForw2}
                  />
                </Form.Field>
                <Form.Field padded="very">
                  <label>Choose Forward 3</label>
                  <Dropdown
                    placeholder='Player'
                    fluid
                    search
                    selection
                    options={PlayersFwd}
                    onChange={this.handleChangeForw3}
                  />
                </Form.Field>
              </Grid.Column>
            </Grid>
          </Form>
        </Segment>
        <Segment basic>

          <Modal
            trigger={
              <div id="button">
                <Button style={this.state.styles} color='purple' size="huge" onClick={this.getScore}>
                  Submit
        </Button>
              </div>
            }>

            <Modal.Header>Select a Photo</Modal.Header>
            <Modal.Content image>
              <Image wrapped size="medium" src="/images/avatar/large/rachel.png" />
              <Modal.Description>
                <Header>Score of the team you picked </Header>
                <p>Game Week: {this.state.gameWeek}</p>
                <p>Score: {this.state.score}</p>
                <p> Goal Keeper 1: {this.state.goalkeeper1}</p>
                <p> Goal Keeper 2: {this.state.goalkeeper2}</p>
                <p> Defender 1: {this.state.defender1}</p>
                <p> Defender 2: {this.state.defender2}</p>
                <p> Defender 3: {this.state.defender3}</p>
                <p> Defender 4: {this.state.defender4}</p>
                <p>Midfielder 1: {this.state.midfielder1}</p>
                <p>Midfielder 2: {this.state.midfielder2}</p>
                <p>Midfielder 3: {this.state.midfielder3}</p>
                <p>Midfielder 4: {this.state.midfielder4}</p>
                <p>Forward 1: {this.state.forward1}</p>
                <p>Forward 2: {this.state.forward2}</p>
                <p>Forward 3: {this.state.forward3}</p>
              </Modal.Description>
            </Modal.Content>
          </Modal>
        </Segment>
      </Container>
    );
  }
}

export default FormExampleSubcomponentControl;
