import React from 'react'
import { Table, Segment, Pagination } from 'semantic-ui-react'
import PlayerBio from './players_bio.json'

const headerRow = ['Name', 'Team', 'Position', 'ID']

function renderBodyRow({ name, team, position, id }, i) {
  if (i <= 5) {
    return {
      key: id,
      cells: [
        name,
        team,
        position,
        id
      ],
    }
  }
}


const PlayerTable = () => (
  <Segment basic padded='very'>
    <Table
      size='small'
      sortable
      color='purple'
      celled
      headerRow={headerRow}
      renderBodyRow={renderBodyRow}
      footerRow={<Pagination defaultActivePage={1} totalPages={5} onPageChange={renderBodyRow} />}
      tableData={PlayerBio}
    />
  </Segment>
)

export default PlayerTable
