import React from 'react'
import { Table, Segment, Pagination } from 'semantic-ui-react'
import PlayerBio from './players_bio.json'

const headerRow = ['Name', 'Team', 'Position', 'ID']
const state = { curr_list_post: 0 }

function handleClick() {
  state.curr_list_post += 5
  renderBodyRow()
}

function renderBodyRow({ name, team, position, id }, i = state.curr_list_post) {
  if (i - state.curr_list_post <= 5) {
    return {
      key: id,
      cells: [
        name,
        team,
        position,
        id
      ],
    }
  }
}


const PlayerTable = () => (
  <Segment basic padded='very'>
    <Table
      size='small'
      sortable
      color='purple'
      celled
      headerRow={headerRow}
      renderBodyRow={renderBodyRow}
      footerRow={<Pagination defaultActivePage={5} totalPages={10} onPageChange={handleClick} />}
      tableData={PlayerBio}
    />
  </Segment>
)

export default PlayerTable
