import React from 'react'
import { Table, Segment, Pagination } from 'semantic-ui-react'
import PlayerBio from './players_bio.json'

export default class PlayerTable extends Component {
  state = {}

  handleClick = () => window.scrollTo({
    top: 1000
  })


  state = { curr_list_post: 0 }



  render() {
    const headerRow = ['Name', 'Team', 'Position', 'ID']
    function renderBodyRow({ name, team, position, id }, i) {
      if (curr_list_post <= 5) {
        return {
          key: id,
          cells: [
            name,
            team,
            position,
            id
          ],
        }
      }

      return (
        <Segment basic padded='very'>
          <Table
            size='small'
            sortable
            color='purple'
            celled
            headerRow={headerRow}
            renderBodyRow={renderBodyRow}
            footerRow={<Pagination defaultActivePage={5} totalPages={10} onPageChange={this.handleClick} />}
            tableData={PlayerBio}
          />
        </Segment>
      )

    }
  }


}
