import React from 'react'
import { Table, Segment } from 'semantic-ui-react'
import PlayerBio from './players_bio.json'

const headerRow = ['Name', 'Team', 'Position', 'ID']
const state = { curr_list_post: 0 }

function renderBodyRow({ name, team, position, id }, curr_list_post) {
  if (curr_list_post <= 5) {
    this.state = { curr_list_post: curr_list_post + 1 }
    return {
      key: id,
      cells: [
        name,
        team,
        position,
        id
      ],
    }
  }

}


const PlayerTable = () => (
  <Segment basic padded='very'>
    <Table
      size='small'
      sortable
      color='purple'
      celled
      headerRow={headerRow}
      renderBodyRow={renderBodyRow}
      tableData={PlayerBio}
    />
  </Segment>
)

export default PlayerTable
