import React, { Component } from "react";
import { Form } from "semantic-ui-react";
import DropdownGK from "./DropdownGK.js";
import DropdownDEF from "./DropdownDEF.js";
import DropdownMID from "./DropdownMID.js";
import DropdownFWD from "./DropdownFWD.js";
import { Container, Divider, Grid, Header, Image } from "semantic-ui-react";
import { Segment } from "semantic-ui-react";
import { Button, Modal } from "semantic-ui-react";

class FormExampleSubcomponentControl extends Component {
  state = {
    defender: [],
    fwd: [],
    list: [],
    styles: {
      position: "relative",
    },
  }

  onUpdateItems = (e, index) => {
    const list = this.state.list;
    list[index].number = e.target.value;
    this.setState({
      list
    });
    console.log(this.state.list);
  };
  // onUpdateItems = (event) => {
  //   // this.setState(state => {
  //   //   const list = state.list.map(item => item + 1);
  //   //   return {
  //   //     list,
  //   //   };
  //   // });

  // };


  handleChange = (e, { value }) => this.setState({ value });
  handleGKChange = (event) => {
    //this.setState({ list });
  }

  render() {
    const { value } = this.state;
    return (
      <Container>
        <Segment basic padded="very">
          <Header as="h2" textAlign="center">
            Team Selection Form
        </Header>
          <Form>
            <Grid columns={2} padded>
              <Grid.Column>
                <Form.Field label="Choose 2 Goal Keepers" control={DropdownGK} onChange={this.onUpdateItems(0)} />
              </Grid.Column>
              <Grid.Column>
                <Form.Field label="Choose 4 Defense" control={DropdownDEF} />
              </Grid.Column>
              <Grid.Column>
                <Form.Field label="Choose 4 Midfielders" control={DropdownMID} />
              </Grid.Column>
              <Grid.Column>
                <Form.Field label="Choose 3 Forwards" control={DropdownFWD} />
              </Grid.Column>

              <div>
                {
                  //<Form.Button>Submit</Form.Button>
                }
              </div>
            </Grid>
          </Form>
        </Segment>
        <Segment basic>
          <Modal
            trigger={
              <div id="button">
                <Button style={this.state.styles} color="blue">
                  Submit
        </Button>
              </div>
            }>

            <Modal.Header>Select a Photo</Modal.Header>
            <Modal.Content image>
              <Image wrapped size="medium" src="/images/avatar/large/rachel.png" />
              <Modal.Description>
                <Header>Score of the team you picked </Header>
                <p>These are the players your team was made of</p>
                <p>Is it okay to use this photo?</p>
              </Modal.Description>
            </Modal.Content>
          </Modal>
        </Segment>
      </Container>
    );
  }
}

export default FormExampleSubcomponentControl;
