import React, { Component } from 'react'
import { Button, Dimmer, Image, Container, Label } from 'semantic-ui-react'
import Menu from './Menu';

export default class Head extends Component {
  state = {}

  handleShow = () => this.setState({ active: true })
  handleHide = () => this.setState({ active: false })

  render() {
    const { active } = this.state
    const content = (
      <div>
        <Button color='teal' size="massive" primary>Pick Your Team</Button>
      </div>
    )

    return (
      <Container>
        <Menu />
        <Dimmer.Dimmable
          as={Image}
          dimmed={active}
          dimmer={{ active, content }}
          onMouseEnter={this.handleShow}
          onMouseLeave={this.handleHide}
          size='fluid'
          src='https://resources.premierleague.com/photos/2019/06/25/1f77171b-6c8d-44bf-88b3-4d474201930a/FantasyLaunch_Editorial_964x643.png?width=930&height=620'
        />
      </Container>
    )
  }
}
